import React ,{useState} from "react";

import "./styles/UpcomingDataForm.css";

const UpcomingDataForm = (props) => {
  //zone to create methods for every event_listener
  //1-saving the target-value
  const [inputTitle, setInputTitle] = useState(props.name);
  const [inputAmount, setInputAmount] = useState(props.amount);
  const [inputDate, setInputDate] = useState(props.date);

  //2-methods to handle input event
  const titleChangeHandler = (event) => {
    // console.log(
    //   "test Handler Title about to change or parameter event-target-value");
      return (
      // console.log("test Title about to change or parameter event-see target-value:?" )
     // console.log(event.target.value)
     setInputTitle(event.target.value)
     
    );
    
  };
 
  const amountChangeHandler = (event) => {
    return  setInputAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    return  setInputDate(event.target.value);
  };
   //make sure saving all target-value using imported {useState}
  //call the function and set it for each event listener input
  return (
    <form>
      {/* add event_listener to attribute for every key stroke show up with label */}
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label> Title:</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label> Amount:</label>
          <input
            type="number"
            onChange={amountChangeHandler}
            min="0.01"
            step="0.01"
          />
        </div>
        <div className="new-expense__control">
          <label> Date:</label>
          <input
            type="date"
            onChange={dateChangeHandler}
            min="2021-01-01"
            max="2022-12-31"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit"> Add Expense</button>
      </div>
    </form>
  );
};

export default UpcomingDataForm;
