import React, { useState } from "react";

import "./styles/UpcomingDataForm.css";

const UpcomingDataForm = (props) => {
  //Zone to create methods for every event_listener
  //A-use state for saving all the target-values
  //option1: save data changed
  //   const [inputTitle, setInputTitle] = useState("");
  //   const [inputAmount, setInputAmount] = useState("");
  //   const [inputDate, setInputDate] = useState("");

  //Alternative approach use one useState - What's better?
  //option2:save data changed
  const [userInputs, setUserInputs] = useState({
    inputTitle: "",
    inputAmount: "",
    inputDate: "",
  });

  //B-Create methods to handle input event
  //option1:
  // const titleChangeHandler = (event) => {
  //console.log('titletHandler')
  // console.log(
  //   "test Handler Title about to change or parameter event-target-value");
  //  return (
  // console.log("test Title about to change or parameter event-see target-value:?" )
  // console.log(event.target.value)
  //  setInputTitle(event.target.value)
  // );
  // };
  //option2:
  const titleChangeHandler = (event) => {
    setUserInputs({
      ...userInputs,
      inputTitle: event.target.value,
    });

    //safer way
  };
  const amountChangeHandler = (event) => {
    //console.log('amountHandler')
    //return setInputAmount(event.target.value);
    setUserInputs({
      ...userInputs,
      inputAmount: event.target.value,
    });
    
  };

  const dateChangeHandler = (event) => {
    //console.log('dateHandler')
    // return setInputDate(event.target.value);
    setUserInputs({
      ...userInputs,
      inputDatet: event.target.value,
    });
  };
  //make sure saving all target-values using imported {useState}
  //call the function and set it for each event listener input
  return (
    <form>
      {/* add event_listener to attribute for every key stroke show up with label */}
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label> Title:</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label> Amount:</label>
          <input
            type="number"
            onChange={amountChangeHandler}
            min="0.01"
            step="0.01"
          />
        </div>
        <div className="new-expense__control">
          <label> Date:</label>
          <input
            type="date"
            onChange={dateChangeHandler}
            min="2021-01-01"
            max="2022-12-31"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit"> Add Expense</button>
      </div>
    </form>
  );
};

export default UpcomingDataForm;
