import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
//import Card from "../UI/Card";

import "../styles/Expenses.css";

//map(m=>m*2): transforms each element of the original array using a function parameter.
//const transArray = originalArray.map(function_parameter)

//note: props receive from parent is an array_object
const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const FilterYearChangedHandler = (selectedOption) => {
    setFilteredYear(selectedOption);
  };
  return (
    // <Card className="expenses">
    <div className="expenses">
      <ExpensesFilter
        selectedYear={filteredYear}
        onFilterChanged={FilterYearChangedHandler}
      />
      {/* transform the parent items_object */}
{props.items.map(outlays=><ExpenseItem title={outlays.name} amount={outlays.amount} title={outlays.name}/>)}

      <ExpenseItem
        date={props.items[0].date}
        name={props.items[0].name}
        amount={props.items[0].amount}
      />
      <ExpenseItem
        date={props.items[1].date}
        name={props.items[1].name}
        amount={props.items[1].amount}
      />
      <ExpenseItem
        date={props.items[2].date}
        name={props.items[2].name}
        amount={props.items[2].amount}
      />
      <ExpenseItem
        date={props.items[3].date}
        name={props.items[3].name}
        amount={props.items[3].amount}
      />
    </div>
  );
};

export default Expenses;
