import React , {useState} from'react'
import ExpenseDate from "./ExpenseDate";
import Cards from '../UI/Card'
import "../styles/ExpenseItem.css";

//child - components function with props parameter
const ExpenseItem=(props)=> {
  //zone for adding javascript codes before returning props_data output

useState();

  const clickTitleHandler= ()=>{
    console.log('The button was fired')
  }
 
  return (
    <Cards className='expense-item'>     
        <ExpenseDate dat={props.dat} />
        <div className="expense-item__description">
          <h2>{props.name}</h2>
          <div className="expense-item__price">${props.amount}</div>
        </div>

{/*event_listener: add a react listener to an event, passing a pointer to the method*/}
        <button onClick={clickTitleHandler}> Change Title</button>
      
    </Cards>
  );
}
export default ExpenseItem;
