import React from "react";

import "./styles/UpcomingDataForm.css";

const UpcomingDataForm = () => {
    //zone to create methods for every event_listener
 const titleChangeHandler = ()=>{
     
     return( 0);
 }
 const amountChangeHandler = ()=>{
    return( 0);
}


const dateChangeHandler = ()=>{
    return( 0);
}
  return (
    <form>
         {/* add event_listener to attribute for every key stroke show up with label */}
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label> Title:</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label> Amount:</label>
          <input type="number" onChange={amountChangeHandler} min="0.01" step="0.01" />
        </div>
        <div className="new-expense__control">
          <label> Date:</label>
          <input type="date" onChange={dateChangeHandler} min="2021-01-01" max="2022-12-31" />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit"> Add Expense</button>
      </div>
    </form>
  );
};

export default UpcomingDataForm;
